import React from 'react';
import styled from 'styled-components';
import {lightGreen} from 'variables';

const StyledButton = styled.button`
    margin: 30px;
    height: 30px;
    width: 60px;
    background-color: ${props => inverseColor ? white: lightGreen}
    color: ${props => inverseColor ? lightGreen: white};
`

const Button = ({type, inverse, onClick, value}) => {
    return <StyledButton inverseColor={inverse} type={type} onClick={onClick} >{value}</StyledButton>
}

export default Button;
