import React, {Component} from 'react';
import styled from 'styled-components';
import {lightGreen} from 'variables';
import LoginPopover from 'components/LoginPopover';

const HeaderContainer = styled.div`
  width: 100%;
  background-color: ${lightGreen};
  height: 40px;
  justify-content: space-between;
  align-items: center;
  display: flex;
`;

const StyledButton = styled.button`
  background-color: white;
  color: blue;
  width: 50px;
  border: none;
  margin: 3px;
  font-size: small;
`

const ButtonContainer = styled.div`
  justify-content: flex-end;
  display:flex;
`

const StyledHeader = styled.h3`
  margin-left:30px;
`


class Header extends Component {
  constructor() {
    super();
    this.state = {
      isLoggingIn: false
    }
  }

  toggleLogin = () => this.setState(currState => ({isLoggingIn: !currState.isLoggingIn}));

  render(){
      return (
              <HeaderContainer>
                <StyledHeader>Calorie Counter App</StyledHeader>
                <ButtonContainer id='CalorieLoggerHeader'>
                  {this.state.isLoggingIn && <LoginPopover rootId='CalorieLoggerHeader'/>}
                  <StyledButton onClick={this.props.toggleSignUp}>Sign Up</StyledButton>
                  <StyledButton onClick={this.toggleLogin}>Login</StyledButton>
                </ButtonContainer>
              </HeaderContainer>
      )
  }
}

export default Header;
