import {assoc, propOr} from 'ramda';


const unauthorizedClearSession = response => {
    if(response.status == '401'){
        sessionStorage.clear();
    }
}
export const signUpUser = user => {
    try {
        return fetch('http://localhost:3000/api/signup', {
            method:'POST',
            mode: 'cors',
            cache: "no-cache",
            url: 'http://localhost:3000',
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(assoc('roles', ['user'], user))
        })
    } catch(err) {
        throw Error(err);
    }
}

export const logInUser = user => {
    try {
        return fetch('http://localhost:3000/api/login', {
            method:'POST',
            mode: 'cors',
            cache: "no-cache",
            url: 'http://localhost:3000',
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(user)
        })
    } catch(err) {
        throw Error(err);
    }
}


export const loadUserMeals = async user => {
    try {
        const mealResponse = await fetch('http://localhost:3000/api/meals', {
            method:'GET',
            mode: 'cors',
            cache: "no-cache",
            url: 'http://localhost:3000',
            headers: {
                "Content-Type": "application/json",
                "Authorization": propOr(null, 'jwt', sessionStorage.getItem('user'))
            },
        })
        unauthorizedClearSession(mealResponse)
        return await mealResponse.json();
    } catch(err) {
        unauthorizedClearSession(err)
        throw Error(err);
    }
}

export const saveNewMeal = meal => {
    try {
        return fetch('http://localhost:3000/api/meals', {
            method:'POST',
            mode: 'cors',
            cache: "no-cache",
            url: 'http://localhost:3000',
            headers: {
                "Content-Type": "application/json",
                "Authorization": propOr(null, 'jwt', sessionStorage.getItem('user'))
            },
            body: JSON.stringify(meal)
        })
    } catch(err) {
        throw Error(err);
    }
}

    export const deleteMeal = mealId => {
        try {
            return fetch(`http://localhost:3000/api/meals/${mealId}`, {
                method:'DELETE',
                mode: 'cors',
                cache: "no-cache",
                url: 'http://localhost:3000',
                headers: {
                    "Authorization": propOr(null, 'jwt', sessionStorage.getItem('user'))
                },
            })
        } catch(err) {
            throw Error(err);
        }
    }

        export const updateUser = (userId, user) => {
            try {
                return fetch(`http://localhost:3000/api/users/${userId}`, {
                    method:'PATCH',
                    mode: 'cors',
                    cache: "no-cache",
                    url: 'http://localhost:3000',
                    headers: {
                        "Content-Type": "application/json",
                        "Authorization": propOr(null, 'jwt', sessionStorage.getItem('user'))
                    },
                    body: JSON.stringify(user)
                })
            } catch(err) {
                throw Error(err);
            }
}
