import TableItem from 'components/TableItem';
import React from 'react';
import {contains} from 'ramda';
const TableItemGroup = ({meals, day, saveMeal, idx, toggleEditing, editingIndexes}) => {
    return meals.map(meal => (<>
                                <h5>{day}</h5>
                                <TableItem 
                                    meal={meal}
                                    toggleEditing={toggleEditing}
                                    isEditing={contains(`item-${idx}`, editingIndexes)} 
                                    id={`item-${idx}`}
                                    saveMeal={saveMeal}
                                />
                              </>))
}

export default TableItemGroup;
