import TableItem from 'components/TableItem';
import React from 'react';
import {contains} from 'ramda';
import styled from 'styled-components';

const Container = styled.div`
    align-items: center;
    display: flex;
    flex-direction: column;
`

const TableItemGroup = ({meals, day, deleteMeal, updateMeal, saveMeal, idx, toggleEditing, calorieMax,  editingItems}) => {
    const actualDayCalories = meals.reduce((cals, meal) => {
        cals += meal.calories;
        return cals;
    }, 0);
    return <Container>
        <h5>{day}</h5>
        {
            meals.map((meal, mealidx) => <TableItem 
                                    meal={meal}
                                    isOverLimit={actualDayCalories > calorieMax}
                                    toggleEditing={toggleEditing}
                                    isEditing={contains(`item-${meal.id}`, editingItems)} 
                                    id={`item-${idx}-${mealidx}`}
                                    key={`item-${idx}-${mealidx}`}
                                    saveMeal={saveMeal}
                                    updateMeal={updateMeal}
                                    deleteMeal={deleteMeal}
                                />
                              )
            }
    </Container>
}

export default TableItemGroup;
