import React, {Component} from 'react';
import styled from 'styled-components';
import TableItem from 'components/TableItem';
import {append, without, contains} from 'ramda';

const TableContainer = styled.div`
  display:flex;
  flex-direction: column;
`

class Table extends Component {
    constructor(){
        super();
        this.state = {
            editingIndexes: []
        }
    }
    toggleEditing = id => {
        contains(id, this.state.editingIndexes) ? 
        this.setState(prevState => ({editingIndexes: without(id, prevState.editingIndexes)})) :
        this.setState(prevState => ({editingIndexes: append(id, prevState.editingIndexes)}))
    }

    render() {
        const {items} = this.props;
        return (
            <TableContainer>{
                items.map((item, idx) => 
                        <TableItem 
                            toggleEditing={this.toggleEditing}
                            isEditing={contains(`item-${idx}`, this.state.editingIndexes)} 
                            id={`item-${idx}`}
                        />
                    )
                }
            </TableContainer>)

    }
}

Table.defaultProps = {
    items:[{}]
}


export default Table;
