import React, {Component} from 'react';
import {Formik} from 'formik';
import FormItem from 'components/FormItem';
import Button from 'components/Button';
import styled from 'styled-components';

const FilterContainer = styled.form`
    display:flex;
    flex-direction: row;
    align-items: center;
    justify-content: center;
    border: 1px solid white
`;

const StyledFilterItem = styled.input`
    border: none;
    display: flex;
    width: 100px;
    height: 30px;
    margin: 3px;
    text-align: center;
`


class Filter extends Component {
    render(){
        const {setFilter, filter} = this.props;

        return <Formik
                    initialValues={filter}
                    onSubmit={values => {
                        return;
                    }}
            >{({
                values,
                errors,
                touched,
                handleChange,
                handleSubmit,
                isSubmitting,
            }) => {
            return <FilterContainer onSubmit={handleSubmit}>
                <StyledFilterItem 
                    type="text"
                    name="startDate"
                    onChange={handleChange}
                    value={values.startDate}
                />
                <StyledFilterItem 
                    type="text"
                    name="endDate"
                    onChange={handleChange}
                    value={values.endDate}
                />
                <StyledFilterItem 
                    type="text"
                    name="startTime"
                    onChange={handleChange}
                    value={values.startTime}
                />
                <StyledFilterItem 
                    type="text"
                    name="endTime"
                    onChange={handleChange}
                    value={values.endTime}
                />
                <Button inverse value='Filter' type='submit' onClick={() => setFilter(this.state)}/>
            </FilterContainer>}
            }

            </Formik>
    }
}

export default Filter;
