import React from 'react';
import styled from 'styled-components';
import Button from 'components/Button';

const Item = styled.div`
    height: 60px;
    width: 90%;
    background-color: white;
    margin-top: 5px;
    margin-left: 5px;
`

class TableItem extends React.Component {
    constructor(props) {
        super();
        this.state = {
            description: props.description,
            calories: props.calories
        }
    }
    render(){
        const {description, date, calories, id, toggleEditing, saveMeal, isEditing = false} = this.props;

        return <Item>
                    <label>Description:</label>
                        {isEditing ? <input initialValue={description}/> : <label>{description}</label>}
                        {isEditing ? <input initialValue={calories}/> : <label>{calories}</label>}
                    <Button value='Delete' />
                    {isEditing ? 
                        <>
                            <Button value='Save' onClick={() => saveMeal(this.state)}/>
                            <Button value='Cancel' onClick={() => toggleEditing(id)} />
                        </> : 
                        <Button value='Edit' onClick={() => toggleEditing(id)} />}
            </Item>
    }
}

export default TableItem;
