import React, {Component} from 'react';
import styled from 'styled-components';
import {lightGreen} from 'variables';
import LoginPopover from 'components/LoginPopover';

const HeaderContainer = styled.div`
  width: 100%;
  background-color: ${lightGreen};
  height: 40px;
  justify-content: flex-end;
  display: flex;
`;

const StyledButton = styled.button`
  background-color: white;
  color: blue;
  width: 50px;
  border: none;
  margin: 3px;
  font-size: small;
`


class Header extends Component {
  constructor() {
    super();
    this.state = {
      isLoggingIn: false
    }
  }

  toggleLogin = () => this.setState(currState => ({isLoggingIn: !currState.isLoggingIn}));

  render(){
      return (
              <HeaderContainer id='CalorieLoggerHeader'>
                <h3>Calorie Counter App</h3>
                {this.state.isLoggingIn && <LoginPopover rootId='CalorieLoggerHeader'/>}
                <StyledButton onClick={this.toggleLogin}>Login</StyledButton>
              </HeaderContainer>
      )
  }
}

export default Header;
