import React, {Component} from 'react';
import styled from 'styled-components';
import {lightGreen} from 'variables';
import {Formik} from 'formik';
import {signUpUser} from 'Api';
import FormItem from 'components/FormItem';
import Button from 'components/Button';

const SignUpContainer = styled.div`
    display: flex;
    flex-direction: column;
    background-color: ${lightGreen};
    align-items: center;
    margin: 40px 80px 40px 80px;
    padding: 20px 0 20px 0;
`;

const SignUpForm = styled.form`
    margin: 5px;
    display: flex;
    flex-direction: column;
    width: 300px;
    align-items: center;
`



const StyledButton = styled.button`
    margin: 30px;
    height: 30px;
    width: 60px;
`



class SignUp extends Component {
    constructor() {
        super();
        this.state = {
            createdUser: null,
            errors: null
        }
    }

    userCreatedForm = () => <div>
            <h4>{`User ${this.state.createdUser.email} created successfully`}</h4>
            <Button onClick={this.props.toggleSignUp} value='Start logging' />
        </div>

    setCreatedUser = user => this.setState({createdUser: user})

    render(){
        return this.state.createdUser ? this.userCreatedForm()
                : <Formik
                    initialValues={{ email: '', password: '', name: '', dailyCalorieGoal: 2000 }}
                    validate={values => {
                        let errors = {};
                        if (!values.email) {
                          errors.email = 'Required';
                        } else if (
                          !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)
                        ) {
                          errors.email = 'Invalid email address';
                        }
                        return errors;
                      }}
                    onSubmit={ async (values, { setSubmitting }) => {
                        setTimeout(() => {
                            setSubmitting(false);
                        }, 400);
                        try {
                            const response = await signUpUser(values);
                            //set the user returned from the promise
                            this.setCreatedUser(await response.json());
                        } catch (err) {
                            this.setState({error: err.message});
                        }

                    }}>
                    {({
                        values,
                        errors,
                        touched,
                        handleChange,
                        handleSubmit,
                        isSubmitting,
                    }) => (<SignUpContainer>
                            <h2>Create an account:</h2>
                                <SignUpForm onSubmit={handleSubmit}>
                                    <FormItem
                                            type="email"
                                            name="email"
                                            handleChange={handleChange}
                                            value={values.email}
                                        />
                                    <FormItem
                                            type="text"
                                            name="name"
                                            handleChange={handleChange}
                                            value={values.name}
                                        />
                                    <FormItem
                                            type="text"
                                            name="daily calorie goal"
                                            handleChange={handleChange}
                                            value={values.dailyCalorieGoal}
                                        />
                                    <FormItem
                                            type="password"
                                            name="password"
                                            handleChange={handleChange}
                                            value={values.password}
                                        />
                                    <label>{this.state.error}</label>
                                    <Button inverse value='Submit' type="submit" disabled={isSubmitting}/>
                                    <Button inverse value='Cancel' onClick={this.props.toggleSignUp} />
                                </SignUpForm>
                    </SignUpContainer> )}
                </Formik>
    }
}

export default SignUp;
