import React from "react";
import styled from "styled-components";
import Button from "components/Button";
import { set, lensPath} from "ramda";

const Item = styled.div`
  height: 60px;
  width: 90%;
  background-color: white;
  margin-top: 5px;
  margin-left: 5px;
`;

class TableItem extends React.Component {
  constructor(props) {
    super();
    this.state = {
      meal: props.meal || {}
    };
  }

  updateState = (value, key) => {
    this.setState(prevState => set(lensPath(['meal', key]), value, prevState.meal));
  };

  render() {
    const {
      description,
      date,
      calories,
      id,
      toggleEditing,
      saveMeal,
      isEditing = false
    } = this.props;
    const { meal } = this.state;

    return (
      <Item>
        <label>Description:</label>
        {isEditing ? (
          <input
            initialValue={description}
            onChange={val => this.updateState(val, "description")}
          />
        ) : (
          <label>{meal.description}</label>
        )}
        <label>Calories:</label>
        {isEditing ? (
          <input
            initialValue={calories}
            onChange={val => this.updateState(val, "calories")}
          />
        ) : (
          <label>{calories}</label>
        )}
        <Button value="Delete" />
        {isEditing ? (
          <>
            <Button value="Save" onClick={() => saveMeal(this.state.meal)} />
            <Button value="Cancel" onClick={() => toggleEditing(id)} />
          </>
        ) : (
          <Button value="Edit" onClick={() => toggleEditing(id)} />
        )}
      </Item>
    );
  }
}

export default TableItem;
