import React, { Component } from 'react';
import './App.css';
import styled from 'styled-components';
import CalorieLogger from 'pages/CalorieLogger';
import SignUp from 'pages/SignUp';

const AppContainer = styled.div`
  height: 100vh;
  width: 100vw;
`;
class App extends Component {
  constructor() {
    state= {
      signUp: false
    }
  }

  toggleSignUp = () => this.setState(currState => ({signUp: !currState.signUp}));

  render() {
    return (
      <div className="App">
        <AppContainer id='appcontainer'>
         {signedIn ? <CalorieLogger toggleSignUp={toggleSignUp}/> : <SignUp toggleSignUp={toggleSignUp}/>}
        </AppContainer>
      </div>
    );
  }
}

export default App;
