import React, {Component} from 'react';
import {Formik} from 'formik';
import FormItem from 'components/FormItem';
import Button from 'components/Button';
import styled from 'styled-components';

const FilterContainer = styled.div`
    display:flex;
    flex-direction: row;
    align-items: center;
    justify-content: center;
    border: 1px solid white
`;

const StyledFilterItem = styled.input`
    border: none;
    display: flex;
    width: 100px;
    height: 30px;
    margin: 3px;
    text-align: center;
`


class Filter extends Component {
    constructor(){
        super();
        this.state = {
            startTime: '00:00:00',
            endTime: '23:59:59',
            startDate: '1900-01-01',
            endDate: '2090-01-01'
        }
    }

    render() {
        const {startDate, endDate, startTime, endTime} = this.state;
        const {setFilter} = this.props;
        return <Formik
                    initialValues={this.state}
                    onSubmit={values => {

                    }}
            >{({
                values,
                errors,
                touched,
                handleChange,
                handleSubmit,
                isSubmitting,
            }) => 
            <FilterContainer>
                <StyledFilterItem 
                    type="text"
                    name="startDate"
                    handleChange={handleChange}
                    value={startDate}
                />
                <StyledFilterItem 
                    type="text"
                    name="endDate"
                    handleChange={handleChange}
                    value={endDate}
                />
                <StyledFilterItem 
                    type="text"
                    name="startTime"
                    handleChange={handleChange}
                    value={startTime}
                />
                <StyledFilterItem 
                    type="text"
                    name="endTime"
                    handleChange={handleChange}
                    value={endTime}
                />
                <Button inverse value='Filter' type='submit' onClick={() => setFilter(this.state)}/>
            </FilterContainer>}

            </Formik>
    }
}

export default Filter;
