import TableItem from 'components/TableItem';
import React from 'react';
import {contains} from 'ramda';
const TableItemGroup = ({meals, day, deleteMeal, saveMeal, idx, toggleEditing, calorieMax,  editingIndexes}) => {
    const actualDayCalories = meals.reduce((cals, meal) => cals += meal.calories);
    return <div>
        <h5>{day}</h5>
        {
            meals.map((meal, mealidx) => <TableItem 
                                    meal={meal}
                                    isOverLimit={actualDayCalories > calorieMax}
                                    toggleEditing={toggleEditing}
                                    isEditing={contains(`item-${idx}-${mealidx}`, editingIndexes)} 
                                    id={`item-${idx}-${mealidx}`}
                                    key={`item-${idx}-${mealidx}`}
                                    saveMeal={saveMeal}
                                    deleteMeal={deleteMeal}
                                />
                              )
            }
    </div>
}

export default TableItemGroup;
