import React, {Component,} from 'react';
import Header from 'components/Header';
import Table from 'components/Table';
import styled from 'styled-components';
import {lightGreen} from 'variables';
import {loadUserMeals, deleteMeal, loadUser, saveNewMeal, updateMeal, updateUser} from 'Api';
import {append, without, equals, contains, filter, complement, concat, set, lensPath, keys, propEq, prepend, groupBy, prop} from 'ramda';
import Button from 'components/Button';
import TableItemGroup from 'components/TableItemGroup';
import moment from 'moment';
import Filter from 'components/Filter';
import { PassThrough } from 'stream';
import { pathToFileURL } from 'url';

const Body = styled.div`
  background-color: ${lightGreen};
  height: 70vh;
  margin: 30px;
  padding: 20px;
  overflow: auto;
`;

const initialState = {
    startTime: '00:00:00',
    endTime: '23:59:59',
    startDate: '1900-01-01',
    endDate: '2090-01-01'
}

const dateConstant = 'YYYY-MM-DD HH:mm:ss'

const convertStringToMoment = date => moment(date, dateConstant).utc()

class CalorieLogger extends Component {
    constructor() {
        super();
        this.state = {
            meals:[],
            editingIndexes: [],
            filter: initialState,
            user: JSON.parse(sessionStorage.getItem('user'))
        }
    }

    componentDidMount(){
        this.loadInitialState();
    }

    componentDidUpdate(prevProps, prevState){
        if(!equals(prevState.user, this.state.user)){
            //load the meals
            this.getMeals();

        }
    }

    setFilter = filters => {
        this.setState(prevState => set(lensPath(['filter']), filters, prevState))
    }

    clearFilter = () => this.setFilter(initialState)

    filterMeals = meals => {
        const {filter} = this.state;
        const startDateTimeString = concat(concat(filter.startDate,' '), filter.startTime);
        const endDateTimeString = concat(concat(filter.endDate,' '), filter.endTime);

        const startDateTime = convertStringToMoment(startDateTimeString);
        const endDateTime = convertStringToMoment(endDateTimeString);

        const isInRange = dateTimeMoment => startDateTime.isBefore(dateTimeMoment) && endDateTime.isAfter(dateTimeMoment)

        return meals.filter(meal => isInRange(convertStringToMoment(prop('created_dt', meal))));

    }

    toggleEditing = id => {
        contains(id, this.state.editingIndexes) ? 
        this.setState(prevState => ({editingIndexes: without(id, prevState.editingIndexes)})) :
        this.setState(prevState => ({editingIndexes: append(id, prevState.editingIndexes)}))
    }

    loadInitialState = async () => {
        try {
            const user = await loadUser();
            const meals = await loadUserMeals();

            this.setState({user: user, meals: meals});
        } catch (err){
            if(err.message === 'Expired Token'){
                this.setState({user: {}})
            }
        }
    }

    getMeals = async () => {
        const meals = await loadUserMeals();
        this.setState({meals: meals});
    }

    saveMeal = async meal => {
        await saveNewMeal(meal)
    }

    updateMeal = async (mealId, meal) => await updateMeal(mealId, meal)

    deleteMeal = async meal => {

        if(meal.id) {
            await deleteMeal(meal.id)
        }
        //remove meal from state
        this.setState(prevState => ({meals: filter(complement(propEq)('id', meal.id), prevState.meals)}));
    }

    setLoggedIn = user => this.setState({user:user});

    addNewMeal= () => {
        this.setState(prevState => ({meals: prepend({"id":0, "created_dt": moment().utc().format(dateConstant)}, prevState.meals)}));
        this.toggleEditing("item-0-0");
    }


    render(){
        const {meals, editingIndexes, filter, user} = this.state;

        const getDay = date => moment(date, 'YYYY-MM-DD HH:mm:ss').format('YYYY-MM-DD')

        const dayGroups = groupBy(meal => getDay(prop('created_dt', meal)))(this.filterMeals(meals));

        return <div>
                    <Header toggleSignUp={this.props.toggleSignUp} setUserLoggedIn={this.setLoggedIn}/>
                    <Body>
                        <h3>User Meals:</h3>
                        <label>Daily Calorie Intake: </label>
                        <input 
                            value={user.daily_calorie_count} 
                            onChange={({target}) => 
                                this.setState(prevState => ({user: set(lensPath(['daily_calorie_count']), target.value, prevState.user)}))
                            }
                        />
                        <Button inverse value='Set Calories' onClick={async () => {
                                    const updatedUser = await updateUser(user.id, {daily_calorie_count: parseInt(this.state.user.daily_calorie_count)});
                                    this.setState({user: updatedUser});
                                }
                            }/>
                        <Button inverse value='add meal' onClick={this.addNewMeal}/>
                        <Filter clearFilter={this.clearFilter} filter={filter} setFilter={this.setFilter}/>
                        <Table >{keys(dayGroups).map((date, idx) => 
                            <TableItemGroup 
                                key={`group-${idx}`}
                                calorieMax={this.state.user.daily_calorie_count}
                                day={date}
                                meals={dayGroups[date]}
                                toggleEditing={this.toggleEditing}
                                editingIndexes={editingIndexes} 
                                idx={idx}
                                saveMeal={this.saveMeal}
                                updateMeal={this.updateMeal}
                                deleteMeal={this.deleteMeal}
                            />
                        )}</Table>

                    </Body>

               </div>
    }
}


export default CalorieLogger;
