import React, {Component,} from 'react';
import Header from 'components/Header';
import Table from 'components/Table';
import styled from 'styled-components';
import {lightGreen} from 'variables';
import {loadUserMeals, deleteMeal, loadUser, saveNewMeal, updateMeal, updateUser} from 'Api';
import {append, prepend, without, equals, isEmpty, contains, filter, complement, concat, set, lensPath, keys, propEq, groupBy, prop} from 'ramda';
import Button from 'components/Button';
import TableItemGroup from 'components/TableItemGroup';
import moment from 'moment';
import Filter from 'components/Filter';
import {headerHeight, inputGreen} from 'variables';

const Body = styled.div`
  background-color: ${lightGreen};
  height: calc(100vh - ${headerHeight} - 70px) ;
  margin: 30px 30px 0 30px;
  padding: 20px;
  overflow: auto;
`;

const ControlPanel = styled.div`
  background-color: ${lightGreen};
  height: 60px;
  display:flex;
  flex-direction:row;
  margin: 30px;
  align-items: center;
  justify-content: center
`;

const StyledInput = styled.input`
    border:none;
    height:30px;
    font-size: 14px;
    margin: 5px;
    padding:5px;
    text-align: center;
    background-color: ${inputGreen}
`

const initialFilterState = {
    startTime: '00:00:00',
    endTime: '23:59:59',
    startDate: '1900-01-01',
    endDate: '2090-01-01'
}

const dateConstant = 'YYYY-MM-DD HH:mm:ss'

const convertStringToMoment = date => moment(date, dateConstant).utc()

const initialState = {
    meals:[],
    editingItems: [],
    filter: initialFilterState,
    user: JSON.parse(sessionStorage.getItem('user')) || {},
};

class CalorieLogger extends Component {
    constructor() {
        super();
        this.state = initialState;
    }

    componentDidMount(){
        if(!isEmpty(this.state.user)){
            this.loadInitialState();
        }
    }

    componentDidUpdate(prevProps, prevState){
        if(!equals(prevState.user, this.state.user)){
            //load the meals
            this.getMeals();

        }
    }

    setFilter = filters => {
        this.setState(prevState => set(lensPath(['filter']), filters, prevState))
    }

    clearFilter = cb => {
        this.setFilter(initialFilterState)
        cb();
    }

    filterMeals = meals => {
        const {filter} = this.state;
        const startDateTimeString = concat(concat(filter.startDate,' '), filter.startTime);
        const endDateTimeString = concat(concat(filter.endDate,' '), filter.endTime);

        const startDateTime = convertStringToMoment(startDateTimeString);
        const endDateTime = convertStringToMoment(endDateTimeString);

        const isInRange = dateTimeMoment => startDateTime.isBefore(dateTimeMoment) && endDateTime.isAfter(dateTimeMoment)

        return meals.filter(meal => isInRange(convertStringToMoment(prop('created_dt', meal))));

    }

    toggleEditing = id => {
        contains(id, this.state.editingItems) ? 
        this.setState(prevState => ({editingItems: without(id, prevState.editingItems)})) :
        this.setState(prevState => ({editingItems: prepend(id, prevState.editingItems)}))
    }

    loadInitialState = async () => {
        const {user} = this.state;
        try {
            const loadedUser = await loadUser(user.id);
            const meals = await loadUserMeals();

            this.setState({user: loadedUser, meals: meals});
        } catch (err){
            if(err.message === 'Expired Token'){
                this.setState({user: {}})
            }
        }
    }

    getMeals = async () => {
        const meals = await loadUserMeals();
        this.setState({meals: meals});
    }

    logout = () => {
        sessionStorage.clear();
        this.setState(initialState)
    }

    saveMeal = async meal => {
        const newMeal = await saveNewMeal(meal);
        this.setState(prevState => ({
                meals: append(newMeal, prevState.meals.filter(complement(propEq)('id', 0)))
            })
        )
    }

    updateMeal = async (mealId, meal) => {
        const updatedMeal = await updateMeal(mealId, meal);
        this.setState(prevState => ({
            meals: append(updatedMeal, prevState.meals.filter(complement(propEq)('id', mealId)))
        }))
    }

    deleteMeal = async meal => {

        if(meal.id) {
            await deleteMeal(meal.id)
        }
        //remove meal from state
        this.setState(prevState => ({meals: filter(complement(propEq)('id', meal.id), prevState.meals)}));
    }

    setLoggedIn = user => this.setState({user:user});

    addNewMeal= () => {
        this.setState(prevState => ({meals: append({"id":0, "created_dt": moment().utc().format(dateConstant)}, prevState.meals)}));
        this.toggleEditing("item-0");
    }


    render(){
        const {meals, editingItems, filter, user} = this.state;

        const getDay = date => moment(date, 'YYYY-MM-DD HH:mm:ss').format('YYYY-MM-DD')

        const dayGroups = groupBy(meal => getDay(prop('created_dt', meal)))(this.filterMeals(meals));

        return <div>
                    <Header logout={this.logout} toggleSignUp={this.props.toggleSignUp} setUserLoggedIn={this.setLoggedIn}/>
                    <Body>
                        <h3>User Meals:</h3>
                        <ControlPanel>
                            <label>Daily Calorie Intake: </label>
                            <StyledInput 
                                value={user.daily_calorie_count} 
                                onChange={({target}) => 
                                    this.setState(prevState => ({user: set(lensPath(['daily_calorie_count']), target.value, prevState.user)}))
                                }
                            />
                            <Button inverse value='Set Calories' onClick={async () => {
                                        const updatedUser = await updateUser(user.id, {daily_calorie_count: parseInt(this.state.user.daily_calorie_count)});
                                        this.setState({user: updatedUser});
                                    }
                                }/>
                            <Button inverse value='add meal' onClick={this.addNewMeal}/>
                        </ControlPanel>

                        <Filter clearFilter={this.clearFilter} filter={filter} setFilter={this.setFilter}/>
                        <Table >{keys(dayGroups).map((date, idx) => 
                            <TableItemGroup 
                                key={`group-${idx}`}
                                calorieMax={user.daily_calorie_count}
                                day={date}
                                meals={dayGroups[date]}
                                toggleEditing={this.toggleEditing}
                                editingItems={editingItems} 
                                idx={idx}
                                saveMeal={this.saveMeal}
                                updateMeal={this.updateMeal}
                                deleteMeal={this.deleteMeal}
                            />
                        )}</Table>

                    </Body>

               </div>
    }
}


export default CalorieLogger;
