import React, {Component} from 'react';
import styled from 'styled-components';
import {lightGreen} from 'variables';
import {Formik} from 'formik';
import {assoc} from 'ramda';
import {signUpUser} from 'Api';

const SignUpContainer = styled.div`
    display: flex;
    flex-direction: column;
    background-color: ${lightGreen};
    align-items: center;
    margin: 40px 80px 40px 80px;
    padding: 20px 0 20px 0;
`;

const SignUpForm = styled.form`
    margin: 5px;
    display: flex;
    flex-direction: column;
    width: 300px;
    align-items: center;
`

const StyledInput = styled.input`
    margin: 5px;
    height: 30px;
    border: none;
    width: 100%;
    padding: 5px;
    font-family: sans-serif;
    font-weight: 500;
    font-size: medium;
`;

const StyledLabel = styled.label`
    margin-left:10px;
`

const StyledButton = styled.button`
    margin: 30px;
    height: 30px;
    width: 60px;
`

const FormItem = styled.div`
    display: flex;
    flex-direction: column;
    align-items: flex-start;
    width: 100%;
    margin: 10px 0 10px 0;
`

class SignUp extends Component {


    render(){
        return <Formik
                    initialValues={{ email: '', password: '', name: '', dailyCalorieGoal: 2000 }}
                    validate={values => {
                        let errors = {};
                        if (!values.email) {
                          errors.email = 'Required';
                        } else if (
                          !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)
                        ) {
                          errors.email = 'Invalid email address';
                        }
                        return errors;
                      }}
                    onSubmit={ async (values, { setSubmitting }) => {
                        setTimeout(() => {
                        alert(JSON.stringify(values, null, 2));
                        setSubmitting(false);
                        }, 400);
                        const newUser = await signUpUser(values);

                    }}>
                    {({
                        values,
                        errors,
                        touched,
                        handleChange,
                        handleBlur,
                        handleSubmit,
                        isSubmitting,
                        /* and other goodies */
                    }) => (<SignUpContainer>
                            <h2>Create an account:</h2>
                                <SignUpForm onSubmit={handleSubmit}>
                                    <FormItem>
                                        <StyledLabel>Email</StyledLabel>
                                        <StyledInput
                                            type="email"
                                            name="email"
                                            onChange={handleChange}
                                            value={values.email}
                                        />
                                    </FormItem>
                                    <FormItem>
                                        <StyledLabel>Name</StyledLabel>
                                        <StyledInput
                                            type="text"
                                            name="name"
                                            onChange={handleChange}
                                            value={values.name}
                                        />
                                    </FormItem>
                                    <FormItem>
                                        <StyledLabel>Daily Calorie Goal</StyledLabel>
                                        <StyledInput
                                            type="text"
                                            name="daily calorie goal"
                                            onChange={handleChange}
                                            value={values.dailyCalorieGoal}
                                        />
                                    </FormItem>
                                    <FormItem>
                                        <StyledLabel>Password</StyledLabel>
                                        <StyledInput
                                            type="password"
                                            name="password"
                                            onChange={handleChange}
                                            onBlur={handleBlur}
                                            value={values.password}
                                        />
                                    </FormItem>
                                    <StyledButton type="submit" disabled={isSubmitting}>
                                        Submit
                                    </StyledButton>
                                </SignUpForm>
                    </SignUpContainer> )}
                </Formik>
    }
}

export default SignUp;
