import {assoc, propOr} from 'ramda';


const unauthorizedClearSession = response => {
    if(response.status == 401){
        sessionStorage.clear();
        return true;
    }
    return false;
}
export const signUpUser = user => {
    try {
        return fetch('http://localhost:3000/api/signup', {
            method:'POST',
            mode: 'cors',
            cache: "no-cache",
            url: 'http://localhost:3000',
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(assoc('roles', ['user'], user))
        })
    } catch(err) {
        throw Error(err);
    }
}

export const loadUser = userId => {
    const jwt = propOr(null, 'jwt', JSON.parse(sessionStorage.getItem('user')))
    if(jwt == null){
        throw 'Expired Token'
    }
    try {
        return fetch(`http://localhost:3000/api/users/${userId}`, {
            method:'GET',
            mode: 'cors',
            cache: "no-cache",
            url: 'http://localhost:3000',
            headers: {
                "Content-Type": "application/json",
                "Authorization": jwt
            },
        })
    } catch(err) {
        throw Error(err);
    }
}


export const logInUser = user => {
    try {
        return fetch('http://localhost:3000/api/login', {
            method:'POST',
            mode: 'cors',
            cache: "no-cache",
            url: 'http://localhost:3000',
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(user)
        })
    } catch(err) {
        throw Error(err);
    }
}


export const loadUserMeals = async () => {
    const jwt = propOr(null, 'jwt', JSON.parse(sessionStorage.getItem('user')))
    if(jwt == null){
        throw 'Expired Token'
    }

    try {
        const mealResponse = await fetch('http://localhost:3000/api/meals', {
            method:'GET',
            mode: 'cors',
            cache: "no-cache",
            url: 'http://localhost:3000',
            headers: {
                "Content-Type": "application/json",
                "Authorization":jwt
            },
        })
        if(unauthorizedClearSession(mealResponse)){
            throw 'Expired Token'
        }
        return mealResponse.json();
    } catch(err) {
        throw Error(err);
    }
}

export const saveNewMeal = meal => {
    const jwt = propOr(null, 'jwt', JSON.parse(sessionStorage.getItem('user')))
    if(jwt == null){
        throw 'Expired Token'
    }
    try {
        return fetch('http://localhost:3000/api/meals', {
            method:'POST',
            mode: 'cors',
            cache: "no-cache",
            url: 'http://localhost:3000',
            headers: {
                "Content-Type": "application/json",
                "Authorization": jwt 
            },
            body: JSON.stringify(meal)
        })
    } catch(err) {
        throw Error(err);
    }
}

    export const updateMeal = (mealId, meal) => {
        const jwt = propOr(null, 'jwt', JSON.parse(sessionStorage.getItem('user')))
        if(jwt == null){
            throw 'Expired Token'
        }

        try {
            return fetch(`http://localhost:3000/api/meals/${mealId}`, {
                method:'PATCH',
                mode: 'cors',
                cache: "no-cache",
                url: 'http://localhost:3000',
                headers: {
                    "Content-Type": "application/json",
                    "Authorization": jwt
                },
                body: JSON.stringify(meal)
            })
        } catch(err) {
            throw Error(err);
        }
    }

    export const deleteMeal = mealId => {
        const jwt = propOr(null, 'jwt', JSON.parse(sessionStorage.getItem('user')))
        if(jwt == null){
            throw 'Expired Token'
        }

        try {
            return fetch(`http://localhost:3000/api/meals/${mealId}`, {
                method:'DELETE',
                mode: 'cors',
                cache: "no-cache",
                url: 'http://localhost:3000',
                headers: {
                    "Authorization": jwt
                },
            })
        } catch(err) {
            throw Error(err);
        }
    }

        export const updateUser = (userId, user) => {
            const jwt = propOr(null, 'jwt', JSON.parse(sessionStorage.getItem('user')))
            if(jwt == null){
                throw 'Expired Token'
            }
            try {
                return fetch(`http://localhost:3000/api/users/${userId}`, {
                    method:'PATCH',
                    mode: 'cors',
                    cache: "no-cache",
                    url: 'http://localhost:3000',
                    headers: {
                        "Content-Type": "application/json",
                        "Authorization": jwt
                    },
                    body: JSON.stringify(user)
                })
            } catch(err) {
                throw Error(err);
            }
}
