import React from 'react';
import ReactDOM from 'react-dom';
const LoginPopover = () => {
    divId = 'CalorieLoggerHeader';

    return ReactDOM.createPortal(this.props.children, document.getElementById(this.divId))
}

export default LoginPopover;
