import React from "react";
import styled from "styled-components";
import Button from "components/Button";
import { set, lensPath} from "ramda";
import {lightGreen} from 'variables';
import moment from 'moment';

const Item = styled.div`
  height: 70px;
  width: 90%;
  background-color: white;
  margin-top: 5px;
  margin-left: 5px;
  display:flex;
  align-items:center;
  cursor: default;
  border-radius: 15px;
  opacity:.9
`;

const PropertyContainer = styled.div`
  width: 50%;
  display:flex;
  flex-direction:column;
`
const PropertyItem = styled.div`
  padding-left: 10px;
  display:flex;
  align-items: flex-start;
`

const StyledInput = styled.input`
  border-top:none;
  border-left:none;
  border-right:none;
  border-bottom: 2px solid ${lightGreen};
  text-weight: 500;
  margin-left: 3px;
`

const StatusIndicator = styled.div`
  border-radius:50%;
  width: 12px;
  height: 12px;
  background-color: ${props => props.isOverLimit ? 'red': 'green'};
  border: 1px solid black
  margin-left:5px;
  opacity: .85
`

// Component displays the basic table item where the user adds descriptions and calories
class TableItem extends React.Component {
  constructor(props) {
    super();
    this.state = {
      meal: props.meal || {}
    };
  }

  updateState = (value, key) => {
    this.setState(prevState => set(lensPath(['meal', key]), value, prevState));
  };

  render() {
    const {
      id,
      toggleEditing,
      saveMeal,
      isEditing = false,
      isOverLimit,
    } = this.props;
    const { meal } = this.state;

    return (
      <Item>
        <StatusIndicator isOverLimit={isOverLimit}/>
        <PropertyContainer>
          <PropertyItem>
            <label>Description:</label>
            {isEditing ? (
              <StyledInput
                defaultValue={meal.description}
                onChange={({target}) => this.updateState(target.value, "description")}
              />
            ) : (
              <label>{meal.description}</label>
            )}
          </PropertyItem>
          <PropertyItem>
            <label>Calories:</label>
            {isEditing ? (
              <StyledInput
                defaultValue={meal.calories}
                onChange={({target}) => this.updateState(target.value, "calories")}
              />
            ) : (
              <label>{meal.calories}</label>
            )}
          </PropertyItem>
          <PropertyItem>
            <label>{`Date Created: ${moment(meal.created_dt, 'YYYY-MM-DD HH:mm:ss').format('YYYY-MM-DD')}`}</label>
          </PropertyItem>
        </PropertyContainer>  
        <Button value="Delete" onClick={() => this.props.deleteMeal(meal)} />
        {isEditing ? (
          <>
            <Button value="Save" onClick={() => {
                    saveMeal(this.state.meal);
                    toggleEditing(id);
                }} />
            <Button value="Cancel" onClick={() => toggleEditing(id)} />
          </>
        ) : (
          <Button value="Edit" onClick={() => toggleEditing(id)} />
        )}
      </Item>
    );
  }
}

export default TableItem;
