import React from "react";
import styled from "styled-components";
import Button from "components/Button";
import { set, lensPath} from "ramda";

const Item = styled.div`
  height: 60px;
  width: 90%;
  background-color: white;
  margin-top: 5px;
  margin-left: 5px;
`;

const PropertyContainer = styled.div`

`

class TableItem extends React.Component {
  constructor(props) {
    super();
    this.state = {
      meal: props.meal || {}
    };
  }

  updateState = (value, key) => {
    this.setState(prevState => set(lensPath(['meal', key]), value, prevState));
  };

  render() {
    const {
      description,
      date,
      calories,
      id,
      toggleEditing,
      saveMeal,
      isEditing = false
    } = this.props;
    const { meal } = this.state;

    return (
      <Item>
        <PropertyContainer>
          <label>Description:</label>
          {isEditing ? (
            <input
              initialValue={description}
              onChange={({target}) => this.updateState(target.value, "description")}
            />
          ) : (
            <label>{meal.description}</label>
          )}
          <label>Calories:</label>
          {isEditing ? (
            <input
              initialValue={meal.calories}
              onChange={({target}) => this.updateState(target.value, "calories")}
            />
          ) : (
            <label>{meal.calories}</label>
          )}
          <label>{`Date Created: ${meal.created_dt}`}</label>
        </PropertyContainer>  
        <Button value="Delete" />
        {isEditing ? (
          <>
            <Button value="Save" onClick={() => {
                    saveMeal(this.state.meal);
                    toggleEditing(id);
                }} />
            <Button value="Cancel" onClick={() => toggleEditing(id)} />
          </>
        ) : (
          <Button value="Edit" onClick={() => toggleEditing(id)} />
        )}
      </Item>
    );
  }
}

export default TableItem;
