import React, {Component} from 'react';
import ReactDOM from 'react-dom';
import styled from 'styled-components';
import {lightGreen, headerHeight} from 'variables';
import {Formik, FormikConsumer} from 'formik';
import Button from 'components/Button';
import FormItem from 'components/FormItem'

const PopoverContainer = styled.div`
    height: 300px;
    width: 400px;
    background-color: ${lightGreen};
    position: relative;
    top: ${headerHeight}
    border-left: 1px solid black;
    border-right: 1px solid black;
    border-bottom: 1px solid black;

`

const StyledForm = styled.form`
    margin: 5px;
`

document.getElementById('CalorieLoggerHeader')
class LoginPopover extends Component {
    constructor() {
        super();
        this.el = document.createElement('div');
    }

    componentDidMount(){
        const {rootId} = this.props
        document.getElementById(rootId).appendChild(this.el);
    }

    componentWillUnmount(){
        const {rootId} = this.props
        document.getElementById(rootId).removeChild(this.el);
    }

    render() {
        return ReactDOM.createPortal(
        <PopoverContainer>
            <Formik>{
                <StyledForm>
                    <FormItem
                        name='email'
                        type='email'
                    />
                    <FormItem
                        name='password'
                        type='password'
                    />
                    <Button value='Login' type='submit'/>
                </StyledForm>
            }
            </Formik>
        </PopoverContainer>, this.el)
    }
}

export default LoginPopover;
