import TableItem from 'components/TableItem';
import React from 'react';
import {contains} from 'ramda';
const TableItemGroup = ({meals, day, deleteMeal, saveMeal, idx, toggleEditing, editingIndexes}) => {
    return <div>
        <h5>{day}</h5>
        {
            meals.map(meal => <TableItem 
                                    meal={meal}
                                    toggleEditing={toggleEditing}
                                    isEditing={contains(`item-${idx}`, editingIndexes)} 
                                    id={`item-${idx}`}
                                    saveMeal={saveMeal}
                                    deleteMeal={deleteMeal}
                                />
                              )
            }
    </div>
}

export default TableItemGroup;
