import React from 'react';
import styled from 'styled-components';
import Button from 'components/Button';

const Item = styled.div`
    height: 60px;
    width: 90%;
    background-color: white;
    margin-top: 5px;
    margin-left: 5px;
`

const TableItem = ({description, date, id, toggleEditing, isEditing = false}) => {
    return <Item>
                <label>Description:</label>
                    {isEditing ? <input initialValue={description}/> : <label>{description}</label>}
                <Button value='Delete' />
                {isEditing ? 
                    <><Button value='Save' />
                    <Button value='Cancel' onClick={() => toggleEditing(id)} /></> : 
                    <Button value='Edit' onClick={() => toggleEditing(id)} />}
            </Item>
}

export default TableItem;
